contracts/core/DocumentSignature.sol.content:
// contracts/Box.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./../interfaces/IDocumentSignature.sol";

contract DocumentSignature is Ownable, IDocumentSignature, EIP712, ERC721URIStorage {
    using Counters for Counters.Counter;
    Counters.Counter private idCount;
    mapping(uint256 => DocumentDetials) documentDetials;
    address userIdentityNFT;
    bytes32 public constant CAST_VOTE =
        keccak256("createDocument(uint256 tokenIds,uint256 documentId,string uri)");

    modifier onlyDocumentOwner(address owner) {
        if (owner != msg.sender) {
            revert DocumentSignature__OnlyOwnerCanCall();
        }
        _;
    }

    constructor(
        address _userIdentityNFT,
        string memory name,
        string memory symbol,
        string memory signingDomain,
        string memory signatureVersion
    ) ERC721(name, symbol) EIP712(signingDomain, signatureVersion) {
        userIdentityNFT = _userIdentityNFT;
    }

    function createDocument(
        bytes memory name,
        bytes memory description,
        string memory uri,
        uint64 signatureStartingPeriod,
        uint64 signatureEndingingPeriod,
        uint256[] memory partiesTokenId
    ) public {
        if (ERC721(userIdentityNFT).balanceOf(msg.sender) == 1) {
            revert DocumentSignature__CreatorIdentityNotExit(msg.sender);
        }
        if (signatureEndingingPeriod == signatureStartingPeriod) {
            revert DocumentSignature__StartingAndEndingValuesNotSome(
                signatureEndingingPeriod,
                signatureStartingPeriod
            );
        }
        idCount.increment();
        // mapping(uint256 => SignatureStatus) storage parties;
        uint256 tokenId = idCount.current();
        uint256 documentId = _documentId(tokenId, name, description, partiesTokenId);

        DocumentDetials storage _documentDetials = documentDetials[documentId];
        for (uint256 index = 0; index < partiesTokenId.length; index++) {
            Party memory p = Party(partiesTokenId[index], SignatureStatus.Deafult);
            // _documentDetials.parties[partiesTokenId[index]] = SignatureStatus.Deafult;
            _documentDetials.parties.push(p);
        }
        uint64 starting = toUint64(block.number) + signatureStartingPeriod;
        _documentDetials.signatureStart = _documentDetials.signatureEnd =
            starting +
            signatureEndingingPeriod;
        _documentDetials.name = name;
        _documentDetials.creator = msg.sender;
        _documentDetials.description = description;
        _documentDetials.status = DocumentState.Pending;
        _documentDetials.tokenId = tokenId;
        _safeMint(msg.sender, tokenId);
        _setTokenURI(tokenId, uri);
        emit DocumentedCrearted(tokenId, documentId, msg.sender);
    }

    function processDocument(
        uint256 documentId,
        // uint256[] memory tokenIds,
        bytes[] memory signatures,
        bool isValidation
    ) public onlyDocumentOwner(documentDetials[documentId].creator) {
        // here we get signature response but oracle from off-chain
        bool isAllvalid = true;
        for (uint i = 0; i < documentDetials[documentId].parties.length; i++) {
            bytes32 digest = _hash(
                documentDetials[documentId].parties[i].tokenId,
                documentId,
                tokenURI(documentDetials[documentId].tokenId)
            );
            address signer = ECDSA.recover(digest, signatures[i]);
            if (
                isValidation &&
                signer !=
                ERC721(userIdentityNFT).ownerOf(documentDetials[documentId].parties[i].tokenId)
            ) {
                revert DocumentSignature__InValidSignature(
                    documentDetials[documentId].parties[i].tokenId
                );
            }
            if (
                !isValidation &&
                signer !=
                ERC721(userIdentityNFT).ownerOf(documentDetials[documentId].parties[i].tokenId)
            ) {
                documentDetials[documentId].parties[i].status = SignatureStatus.InValid;
                isAllvalid = false;
            } else {
                documentDetials[documentId].parties[i].status = SignatureStatus.Valid;
            }
        }
        if (isAllvalid) {
            documentDetials[documentId].status = DocumentState.Succeeded;
        } else {
            documentDetials[documentId].status = DocumentState.Executed;
        }
        emit DocumentProcess(documentId, isValidation);
    }

    function getDocumentDetails(uint256 documentId) public view returns (DocumentDetials memory) {
        DocumentDetials memory _documentDetails = documentDetials[documentId];
        _documentDetails.status = getStatus(documentId);
        return _documentDetails;
    }

    function checkMyCastedVote(uint256 documentId) public view returns (SignatureStatus) {
        bool isNotExist = false;
        for (uint i = 0; i < documentDetials[documentId].parties.length; i++) {
            if (
                msg.sender ==
                ERC721(userIdentityNFT).ownerOf(documentDetials[documentId].parties[i].tokenId)
            ) {
                return documentDetials[documentId].parties[i].status;
            }
        }
        if (!isNotExist) {
            revert DocumentSignature__UserNotExist();
        }
        return SignatureStatus.Deafult;
    }

    function getDocumentStartingTime(uint256 documentId) public view returns (uint256) {
        return documentDetials[documentId].signatureStart;
    }

    function getDocumentEndingingTime(uint256 documentId) public view returns (uint256) {
        return documentDetials[documentId].signatureEnd;
    }

    function getStatus(uint256 documentId) public view returns (DocumentState) {
        if (
            documentDetials[documentId].status == DocumentState.Succeeded ||
            documentDetials[documentId].status == DocumentState.Executed
        ) {
            return documentDetials[documentId].status;
        }

        if (documentDetials[documentId].signatureStart > toUint64(block.number)) {
            return DocumentState.Pending;
        }
        if (
            documentDetials[documentId].signatureStart <= toUint64(block.number) &&
            documentDetials[documentId].signatureEnd > toUint64(block.number)
        ) {
            return DocumentState.Active;
        }
        if (documentDetials[documentId].signatureEnd < toUint64(block.number)) {
            return DocumentState.Queued;
        }
        return DocumentState.Pending;
    }

    function getCurrentTime() public view returns (uint256) {
        return toUint64(block.number);
    }

    function _hash(
        uint256 tokenIds,
        uint256 documentId,
        string memory uri
    ) internal view returns (bytes32) {
        return _hashTypedDataV4(keccak256(abi.encode(CAST_VOTE, tokenIds, documentId, uri)));
    }

    function _documentId(
        uint256 tokenId,
        bytes memory name,
        bytes memory description,
        uint256[] memory partiesTokenId
    ) internal pure returns (uint256) {
        return uint256(keccak256(abi.encode(tokenId, name, description, partiesTokenId)));
    }

    function toUint64(uint256 value) internal pure returns (uint64) {
        require(value <= type(uint64).max, "SafeCast: value doesn't fit in 64 bits");
        return uint64(value);
    }

    // function createDocument(
    //     bytes memory name,
    //     bytes memory description,
    //     string memory uri,
    //     uint256 signatureStartingPeriod,
    //     uint256 signatureEndingingPeriod,
    //     uint256[] memory partiesName
    // ) external override {}

    // constructor(string memory name, string memory version) override(EIP712, ERC721) {}
}

contracts/core/FigurePrintOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import "@chainlink/contracts/src/v0.8/ConfirmedOwner.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./../interfaces/IFigurePrintOracle.sol";
import "./OrcaleUrlProvider.sol";

/**
 * Request testnet LINK and ETH here: https://faucets.chain.link/
 * Find information on LINK Token Contracts and get the latest ETH and LINK faucets here: https://docs.chain.link/docs/link-token-contracts/
 */

/**
 * THIS IS AN EXAMPLE CONTRACT WHICH USES HARDCODED VALUES FOR CLARITY.
 * THIS EXAMPLE USES UN-AUDITED CODE.
 * DO NOT USE THIS CODE IN PRODUCTION.
 */

contract FigurePrintOracle is
    ChainlinkClient,
    ConfirmedOwner,
    IFigurePrintOracle,
    AccessControl,
    ReentrancyGuard
{
    using Chainlink for Chainlink.Request;
    bytes32 private constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    mapping(bytes32 => address) private userVerficationRequest;
    mapping(uint => string) private apis;

    mapping(address => VerifcaitonRecord) private userVerficationRecord;
    mapping(address => uint256) private amounts;
    OrcaleUrlProvider orcaleUrlProvider;
    bytes32 private jobId;
    uint256 private fee;
    string public baseUrl;

    // Modifiers
    modifier onlyVerifier() {
        if (!hasRole(VERIFIER_ROLE, msg.sender)) revert FigurePrintOracle__NotVerifer();
        _;
    }

    /**
     * @notice Initialize the link token and target oracle
     *
     * Goerli Testnet details:
     * Link Token: 0x326C977E6efc84E512bB9C30f76E30c160eD06FB
     * Oracle: 0xCC79157eb46F5624204f47AB42b3906cAA40eaB7 (Chainlink DevRel)
     * jobId: ca98366cc7314957b8c012c72f05aeeb
     *
     */
    constructor(
        address _linkToken,
        address _oricle,
        bytes32 _jobId,
        uint256 _fee,
        address _orcaleUrlProvider
    ) ConfirmedOwner(msg.sender) {
        setChainlinkToken(_linkToken);
        setChainlinkOracle(_oricle);
        jobId = _jobId;
        fee = _fee; //(1 * LINK_DIVISIBILITY) / 10; // 0,1 * 10**18 (Varies by network and job)
        orcaleUrlProvider = OrcaleUrlProvider(_orcaleUrlProvider);

        apis[0] = "getAddress";
        apis[1] = "getVerfity";
    }

    //// receive
    receive() external payable {
        amounts[msg.sender] += msg.value;
        emit ReceivedCalled(msg.sender, msg.value);
    }

    /**
     * Create a Chainlink request to retrieve API response, find the target
     * data, then multiply by 1000000000000000000 (to remove decimal places from data).
     */
    function verifyFingerPrint(
        address userAddress,
        string memory userId,
        bytes memory fingerPrint
    ) public onlyVerifier nonReentrant {
        //if record exist and pending
        uint numberTries = 0;
        if (userVerficationRecord[userAddress].status == VerficationStatus.PENDING) {
            revert FigurePrintOracle__RequestAlreadyExist(userAddress);
        } else if (userVerficationRecord[userAddress].status == VerficationStatus.VERIFIED) {
            revert FigurePrintOracle__VerficationAlreadyDone(userAddress);
        } else if (
            userVerficationRecord[userAddress].status == VerficationStatus.FAIL &&
            userVerficationRecord[userAddress].numberTries > 3
        ) {
            revert FigurePrintOracle__ExceedNumberTries(userAddress);
        } else if (userVerficationRecord[userAddress].status == VerficationStatus.FAIL) {
            numberTries++;
        }
        Chainlink.Request memory req = buildChainlinkRequest(
            jobId,
            address(this),
            this.fulfillMultipleParameters.selector
        );

        // Set the URL to perform the GET request on
        baseUrl = orcaleUrlProvider.getURL();
        req.add(
            "get",
            string(
                abi.encodePacked(baseUrl, apis[1], "?userId=", userId, "&fingerPrint=", fingerPrint)
            )
        );
        req.add("path1", "verficationResponse"); //resposnse from api
        req.add("get", string(abi.encodePacked(baseUrl, apis[0], "?address=", userAddress)));
        req.add("path2", "uri");
        // // Sends the request
        bytes32 requestId = sendChainlinkRequest(req, fee);
        userVerficationRequest[requestId] = userAddress;
        userVerficationRecord[userAddress] = VerifcaitonRecord(
            userId,
            numberTries,
            "",
            VerficationStatus.PENDING
        );
        emit VerifyFingerPrint(userId, requestId, userAddress);
    }

    /**
     * @notice Fulfillment function for multiple parameters in a single request
     * @dev This is called by the oracle. recordChainlinkFulfillment must be used.
     */
    function fulfillMultipleParameters(
        bytes32 _requestId,
        string memory isVerfied,
        string memory uri
    )
        public
        // string memory uri
        recordChainlinkFulfillment(_requestId)
    {
        VerficationStatus _status;
        if (keccak256(bytes(isVerfied)) == keccak256(bytes("true"))) {
            _status = VerficationStatus.VERIFIED;
        } else {
            _status = VerficationStatus.FAIL;
        }
        userVerficationRecord[userVerficationRequest[_requestId]].status = _status;
        userVerficationRecord[userVerficationRequest[_requestId]].uri = uri;
        emit VerifationResponse(userVerficationRequest[_requestId], _requestId, uri, isVerfied);
    }

    /// @notice this allow Buyer whose offer is expire or over by other buyer .
    function withdrawLink() public payable nonReentrant {
        uint256 amount = amounts[msg.sender];
        if (amount == 0) revert FigurePrintOracle__NoAmountForWithDraw();
        amounts[msg.sender] = 0;
        (bool success, ) = msg.sender.call{value: amount}("");
        if (!success) {
            revert FigurePrintOracle__FailToWithDrawAmount();
        }
        emit WithDrawAmount(msg.sender, amount);
    }

    function getUserRecord(address userAddress) public view returns (VerifcaitonRecord memory) {
        return userVerficationRecord[userAddress];
    }

    function getBaseURI() public view returns (string memory) {
        return baseUrl;
    }

    function setChainLinkToken(address linkToken) public onlyOwner nonReentrant {
        super.setChainlinkToken(linkToken);
    }

    function setChainLinkOracle(address oricle) public onlyOwner nonReentrant {
        super.setChainlinkOracle(oricle);
    }

    function setJobId(bytes32 _jobId) public onlyOwner nonReentrant {
        jobId = _jobId;
    }

    function setFee(uint256 _fee) public onlyOwner nonReentrant {
        fee = _fee;
    }

    function setVeriferRole(address verifer) public onlyOwner nonReentrant {
        _setupRole(VERIFIER_ROLE, verifer);
    }

    function burnUserRecord(address userAddress) public onlyVerifier nonReentrant {
        userVerficationRecord[userAddress].status = VerficationStatus.DEAFULT;
    }
}

contracts/core/governance_standard/Gover.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/governance/Governor.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorCountingSimple.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorVotes.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorVotesQuorumFraction.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorTimelockControl.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorSettings.sol";

contract GovernorContract is
    Governor,
    GovernorSettings,
    GovernorCountingSimple,
    GovernorVotes,
    GovernorVotesQuorumFraction,
    GovernorTimelockControl
{
    bytes32 public constant CAST_VOTE =
        keccak256("CasteVote(uint256 proposalId,uint8 support,string reason)");

    constructor(
        IVotes _token,
        TimelockController _timelock,
        uint256 _quorumPercentage,
        uint256 _votingPeriod,
        uint256 _votingDelay
    )
        Governor("GovernorContract")
        GovernorSettings(
            _votingDelay /* 1 block */, // voting delay it will tell the after how many block voting will start
            _votingPeriod, // 45818, /* 1 week */ // how long voting will go
            0 // proposal threshold it mean anyone can make purposeal
        )
        GovernorVotes(_token)
        GovernorVotesQuorumFraction(_quorumPercentage) // tell how much percentage of people will vote 4% will vote the it wll be valid
        GovernorTimelockControl(_timelock)
    {}

    function votingDelay() public view override(IGovernor, GovernorSettings) returns (uint256) {
        return super.votingDelay();
    }

    function votingPeriod() public view override(IGovernor, GovernorSettings) returns (uint256) {
        return super.votingPeriod();
    }

    // The following functions are overrides required by Solidity.

    function quorum(
        uint256 blockNumber
    ) public view override(IGovernor, GovernorVotesQuorumFraction) returns (uint256) {
        return super.quorum(blockNumber);
    }

    function getVotes(
        address account,
        uint256 blockNumber
    ) public view override(IGovernor, Governor) returns (uint256) {
        return super.getVotes(account, blockNumber);
    }

    function state(
        uint256 proposalId
    ) public view override(Governor, GovernorTimelockControl) returns (ProposalState) {
        return super.state(proposalId);
    }

    /**
     * This very important function which we user to propose the new change
     * and everyone vote on the proposal
     *
     */
    function propose(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        string memory description
    ) public override(Governor, IGovernor) returns (uint256) {
        return super.propose(targets, values, calldatas, description);
    }

    function proposalThreshold()
        public
        view
        override(Governor, GovernorSettings)
        returns (uint256)
    {
        return super.proposalThreshold();
    }

    function _execute(
        uint256 proposalId,
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) internal override(Governor, GovernorTimelockControl) {
        super._execute(proposalId, targets, values, calldatas, descriptionHash);
    }

    function _cancel(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) internal override(Governor, GovernorTimelockControl) returns (uint256) {
        return super._cancel(targets, values, calldatas, descriptionHash);
    }

    function _executor()
        internal
        view
        override(Governor, GovernorTimelockControl)
        returns (address)
    {
        return super._executor();
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(Governor, GovernorTimelockControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function _hash(
        uint256 proposalId,
        uint8 support,
        string memory reason
    ) internal view returns (bytes32) {
        return _hashTypedDataV4(keccak256(abi.encode(CAST_VOTE, proposalId, support, reason)));
    }

    function getSignature(string memory proposalId) public {
        // first we call here connect with oricel contract to send request and signature based on proposal
        // and map to request id and proposalId so cast vote in future
    }

    /**
     * @dev See {IGovernor-castVoteBySig}.
     */
    function castVoteBySig(
        //requestId this ID will be ID of Oracles response
        uint256 proposalId,
        uint8 support,
        string memory reason,
        bytes memory signature
    ) public virtual returns (uint256) {
        // here we get signature response but oracle from off-chain
        bytes32 digest = _hash(proposalId, support, reason);
        address voter = ECDSA.recover(digest, signature);
        return super._castVote(proposalId, voter, support, "");
    }
}

